chrome.runtime.onInstalled.addListener((e=>{"install"===e.reason?console.log("확장 프로그램이 설치되었습니다."):"update"===e.reason&&console.log("확장 프로그램이 업데이트되었습니다.")})),chrome.action.onClicked.addListener((e=>{console.log("확장 프로그램 아이콘이 클릭되었습니다."),e.url.startsWith("chrome://")?console.warn("Cannot access a chrome:// URL"):chrome.scripting.executeScript({target:{tabId:e.id},files:["content/contentScript.js"]}).then((()=>{console.log("Reading mode enabled.")})).catch((e=>{console.error("Error executing script:",e)}))})),chrome.runtime.onMessage.addListener(((e,o,n)=>{"greet"===e.type&&(console.log("콘텐츠 스크립트로부터 인사 메시지를 받았습니다."),n({response:"안녕하세요! 백그라운드에서 응답합니다."}))}));